from flask import Flask, request, jsonify

app = Flask(__name__)

@app.route('/')
def index():
    return jsonify("Home")

@app.route('/about')
def about():
    data: str = """
    We need your help to beat the first order out of the galaxy.
    """

    response = jsonify(data)
    response.headers.add('Access-Control-Allow-Origin', '*')
    return response


@app.route('/Class')
def news():
    data = {
        "Classes": [
            {"titulo": "Soldier", "image":"https://static.wikia.nocookie.net/ptstarwars/images/4/46/Stormtrooper_Anovos.png/revision/latest?cb=20170103164542"},
            {"titulo": "Crunchyroll release calendar for the week of May 1st to 7th", "image":"data:image/jpeg;base64,"}
        ]
    }

    response = jsonify(data)
    response.headers.add('Access-Control-Allow-Origin', '*')
    return response
if __name__ == "__main__":
    app.run(host='0.0.0.0', port=5000)